function countingValleys(n, s) {
  let stack = [];
  let count = 0;

  for (let char of s) {
    if (!stack.length) stack.push(char);
    else {
      const last_value = stack[stack.length - 1];
      if (last_value != char) stack.pop();
      else stack.push(char);

      if (!stack.length && last_value == 'D') count++;
