function solution(skill, skill_trees) {
  const tress_len = skill_trees.length;
  const pattern = new RegExp('[^' + skill + ']', 'g');
  let answer = 0;

  for (let i = 0; i < tress_len; i++) {
    const skill_check = skill_trees[i].replace(pattern, '');
